#include<bits/stdc++.h>
using namespace std;
int solve(vector<int> v,int key)
{
    int n=v.size();
    int maxElement=v[0];
       int x;
    for(int i=0;i<n;i++)
        maxElement=max(maxElement,v[i]);
       
       if(maxElement>n)
       x=maxElement+1;
       else
       x=n+1;

    cout<<"X="<<x;
    vector<int> map(x,0);
    for(int i=0;i<n-1;i++)
    {
        if(v[i]==key)
        {
            map[v[i+1]]++;
        }
    }
    int maxfreqIndex=0;
    for(int i=0;i<x;i++)
    {
        cout<<"i "<<i<<" = "<<map[i]<<endl;
        if(map[maxfreqIndex]<map[i])
        {
            maxfreqIndex=i;
        }
    }
    return maxfreqIndex;
}
